[25] = {heroId=25, name="Yamazaki", cname="山口龙二", star2={effect="abc",title="饿狼传说",article="孤儿暗黑蛇王,为求生存,他学懂了生存之道,发现自己只有从狼身上才能体验到力量。"}, star3={effect="abc",title="断头台",article="龙二总是不会怜悯敌人,战胜敌人后,他永远能从战斗中领悟到为什么自己会胜利。"}, star4={effect="abc",title="群狼嚎",article="与棍王的相遇,让他明白群攻战术才能获得更大的发挥。"}, star5={effect="abc",title="狂经脉",article="每杀一个人,他都会进入疯狂,而这个疯狂,也是带给他五金能力的原因。"}, },
[26] = {heroId=26, name="Mary", cname="布鲁唛莉", star2={effect="abc",title="自己的路",article="善于擒拿术的她,在与人战斗时,往往会将对方搞脱臼,骨头碎裂的声音,便是领悟之时。"}, star3={effect="abc",title="饿狼",article="与暗黑蛇王和棍王相遇,他们的战斗让自己觉得非常特别,也开始学习他们的能力。"}, star4={effect="abc",title="觉悟",article="发现被骗的自己,明白了一些道理,在决心做好人的时候明白了体术的奥义。"}, star5={effect="abc",title="真之战",article="拳皇之争,战胜了大量的女选手,而自己也明白了,真正的战斗还在远方。"}, },
[27] = {heroId=27, name="Billy", cname="毕厉", star2={effect="abc",title="旋风之章",article="擅长棍术的他,在与敌人战斗中能自身领悟自身的打法,以提升自己的能力。"}, star3={effect="abc",title="大回转蹴",article="在一次与暗黑蛇王的见面后,认清他有狼的本色,也领悟了自己的能力。"}, star4={effect="abc",title="强袭飞翔",article="被多个敌人围攻,身受重伤,却火了下来,棍术已经超越了很多其他的高手。"}, star5={effect="abc",title="旋元杀",article="只有他的武器和他一起度过的日子,他才能安心,最终,他领悟了最后奥义-旋元杀。"}, },
[28] = {heroId=28, name="Iori", cname="邪炎之庵", star2={effect="abc",title="紫炎",article="庵是八神家族最年轻的后裔,继承家族的能力,领悟自身操控紫色火焰的实力。"}, star3={effect="abc",title="琴月阴",article="与草薙京为宿敌,在不停的战斗中,明白了真正的战斗才刚刚开始。"}, star4={effect="abc",title="八稚女",article="与千鹤的组合,让他收心并获得了八尺琼勾玉,也得到了能力的提升。"}, star5={effect="abc",title="狂禁千",article="误接触了疯狂之血,让他变得更加疯狂,战斗力和实力都得到了质的飞跃。"}, },
[29] = {heroId=29, name="Shingo", cname="吾吹真吾", star2={effect="abc",title="俺式",article="真吾找到草薙京,并拜其为师,从中学到一些新的招式,领悟“俺式”。"}, star3={effect="abc",title="真战",article="为了测试自己的水准,参加了个人预选赛,胜出的结果让真吾明白了真正的战斗为何物。"}, star4={effect="abc",title="濒死复生",article="真吾受到影神千鹤的委托,说服京和苍炎之庵组队参赛,虽有生命危险,但领悟了新的奥义。"}, star5={effect="abc",title="死战之章",article="八神突然暴走,真吾与之对抗,逐渐明白了终极高手之间的战斗威力。"}, },
[30] = {heroId=30, name="Orochi", cname="太阳神", star2={effect="abc",title="大蛇之火",article="他的出现,被传闻是草剃家族用草剃剑砍下八歧大蛇后的头而来,命运之轮已经转动。"}, star3={effect="abc",title="破蛹蝶舞",article="解除封印的他,得到的力量无法估量,直接领悟神之奥义。"}, star4={effect="abc",title="火阑降",article="从觉醒的八杰集中体会到自身作为神的使命,得到新的领悟。"}, star5={effect="abc",title="神意",article="从各种挑战者的失败中明白,自己是神的事实,已经无法替代的领悟战之真奥义。"}, },
[34] = {heroId=34, name="Iori2", cname="暴走之庵", star2={effect="abc",title="血之盟约",article="签订血之盟约后，八神一族拥有了强大的苍焰！同时也注入了未知的狂暴血性"}, star3={effect="abc",title="神族的王子",article="击败了天国神族四天王之一暴风，但是飘散在风中的大蛇血液，让八神陷入了疯狂状态。"}, star4={effect="abc",title="暴走涡旋",article="由于感受到大蛇的力量，八神与冷刀安娜在赛场中双双暴走！"}, star5={effect="abc",title="血之骚动",article="月夜下，苍炎之庵施放八稚女，却无意识进入暴走状态，血之骚动再一次唤醒！"}, },
}
