module(...,package.seeall)
Config={
[1] = {id=1, title="闯关规则", txt="<font color='255,255,255'>1.战队等级达到14级开启！<br/>2.逐级挑战每个Boss！<br/>3.每天每个Boss最多挑战3次！<br/>4.每挑战成功一个Boss将获得奖励！<br/>5.每天0点副本自动重置。<br/>6.拿最高通关分数！上排行榜受万人膜拜！</font>", },
[2] = {id=2, title="世界巡回赛规则", txt="<font color='255,255,255'>1.挑战次序：挑战副本成功后，方可挑战下一副本<br/>2.战斗规则：巡回赛挑战中，敌我双方气血值和能量值不会重置。<br/>3.已阵亡英雄不会复活，且不可再上阵。<br/>4.挑战次数：未通关的副本可重复挑战。<br/>4.每日首次打过的关卡，可以一键扫荡。<br/>5.重置次数：每天0点重置挑战次数，但不会重置当前的副本进度。</font>", },
[3] = {id=3, title="大蛇八杰规则", txt="<font color='255,255,255'>1.挑战顺序：每成功挑战一个BOSS，才会获得下一个BOSS的挑战权<br/>2.挑战次数：可无限次挑战BOSS，直至BOSS被打败<br/>3.当前层数内，挑战胜利的英雄无法再次上场。<br/>4.每日首次打过的关卡，可以一键扫荡。<br/>5.重置时间：每天0点重置挑战。</font>", },
[4] = {id=4, title="竞技场规则", txt="1.战斗规则：竞技场内实行自动战斗制度<br></br>2.出战阵容：决定竞技场内上阵挑战的阵容，和被挑战时的阵容<br></br>3.难度分级：根据玩家竞技场等级，随机选出“困难”“中等”“简单”三种类型的选手。<br></br>4.攻占排名：战斗胜利时，被挑战方的排名高于挑战方，则双方排名互换。<br></br>5.挑战次数：每天可免费发起挑战5次，挑战成功或失败都会消耗次数<br></br>6.次数重置：玩家也可以花费钻石购买额外的挑战次数。<br></br>7.挑战奖励：成功挑战玩家，可获得竞技积分奖励，竞技积分可在竞技场商店购买商品。<br></br>8.排名奖励：每天20点，根据竞技场排名，通过邮件发放奖励<br></br>每日排名奖励规则：<br></br>根据每天20:00结算时的排名发放奖励邮件，详细奖励规则如下：<br></br>第1名：<img src='res/common/icon/coin/money.png'></img>×150000  <img src='res/common/icon/coin/arena.png'></img>×5000  <img src='res/common/icon/coin/train.png'></img>×100<br></br>第2-5名：<img src='res/common/icon/coin/money.png'></img>×120000  <img src='res/common/icon/coin/arena.png'></img>×4000  <img src='res/common/icon/coin/train.png'></img>×80<br></br>第6-20名：<img src='res/common/icon/coin/money.png'></img>×100000  <img src='res/common/icon/coin/arena.png'></img>×3500  <img src='res/common/icon/coin/train.png'></img>×70<br></br>第21-50名：<img src='res/common/icon/coin/money.png'></img>×90000  <img src='res/common/icon/coin/arena.png'></img>×3000  <img src='res/common/icon/coin/train.png'></img>×60<br></br>第51-100名：<img src='res/common/icon/coin/money.png'></img>×75000  <img src='res/common/icon/coin/arena.png'></img>×2500  <img src='res/common/icon/coin/train.png'></img>×50<br></br>第101-200名：<img src='res/common/icon/coin/money.png'></img>×60000  <img src='res/common/icon/coin/arena.png'></img>×2000  <img src='res/common/icon/coin/train.png'></img>×40<br></br>......<br></br>", },
[5] = {id=5, title="鲜花赠送规则", txt="<font color='255,255,255'>1.送花奖励次数：每天给心爱的TA首次送花，会扣减1次次数，并可获得相应的体力奖励<br/>2.1朵鲜花/9朵鲜花：有送花奖励次数时，才可赠送，且每天只可对同一位玩家赠送1次<br/>3.99朵鲜花：每天不限次数赠送<br/> <br/>注：送花奖励次数为0，或对同一个玩家重复送花，都不再获得体力奖励。</font>", },
[6] = {id=6, title="公会德州规则", txt="1.玩家每次玩德州游戏，都会增加德州经验，达到指定数值，则公会德州的等级提升。<br></br>2.当前德州的等级，会影响玩家每次德州获得的公会积分及金币。<br></br>3.排行榜中显示本周德州排名最高的玩家及牌型，如果出现相同牌型，谁后获得显示谁。<br></br>4.博彩的牌型如下——<br></br>    皇家同花顺：同花色的AKQJ10<br></br>    同花顺：同花色的单顺<br></br>    四条：四张相同点数的牌加一个单张<br></br>    葫芦：三张相同点数的牌加一个对子<br></br>    同花：想同花色没有顺序的五张牌<br></br>    顺子：不同花色的五张连续的牌<br></br>    三条：三张相同点数加上两张不同点数的牌<br></br>    两对：两对两张相同点数的牌加上一张不同点数牌<br></br>    一对：只出现一个对子，其他三张为不连续的牌且不相同点数的牌<br></br>    单只：任何不符合之前9种牌型的牌<br></br>6.每天每个玩家可免费发起挑战5次，每天凌晨0点整重置次数。<br></br>", },
[7] = {id=7, title="公会踢馆规则", txt="1.踢馆中，会按照当前公会总战力，列出服务器中总战力最接近的5个公会。<br></br>2.点进列出的公会项目，会按照当前玩家战力，列出跟玩家战力最接近的5个玩家。<br></br>3.列出的玩家其队伍构成即该玩家在竞技场中的队伍配置。<br></br>4.玩家可以选择其中的玩家进行挑战，胜利即可以获得一定的公会积分。", },
[8] = {id=8, title="公会奶茶店规则", txt="1.“最新获得的可调制饮品”是指当前奶茶店的等级下，最新开启的可调制饮品，并非只能出产列出的饮品。<br></br>2.玩家每次调制都会增加奶茶店经验，经验达到某个值时，公会奶茶店等级提升。<br></br>3.调制饮品有三种方式，每种消耗的金币不同，同时产出的成酒也会有所不同。<br></br>5.各种饮品及其作用请各位玩家自行发掘和体验！", },
[9] = {id=9, title="签到奖励说明", txt="每月累计签到天数，即可领取对应的签到奖励。<br></br>特定的累计天数，达到指定VIP等级及以上的玩家可以领取双倍奖励！<br></br>当天未领取的奖励隔天即不能领取。", },
[10] = {id=10, title="疯狂之源说明", txt="1.疯狂之源为定点开放活动。<br></br>2.玩家要在指定时间内挑战5名沾染疯狂之血的失控英雄。<br></br>3.造成的伤害越高，排名越靠前。<br></br>5.输出伤害越高，获得的奖励越丰富。<br></br>6.活动时间为：14:00-14:30。", },
[11] = {id=11, title="巅峰竞技场规则", txt="<font color='255,255,255'>1.巅峰竞技场为定点开放，开放后，玩家可以在界面选择寻找对手，系统会自动分配玩家进行战斗。<br/>2.战斗流程，系统匹配到玩家后，进入选择界面，此时需要选择去掉对方3个英雄，之后双方确定后，进入备战界面，选择上阵英雄后点击“开始战斗”进行交战。<br/>3.获胜的玩家可以获得大量积分，失败的玩家获得少量积分，但中途逃跑的玩家不会获得积分。<br/>4.参加“巅峰竞技场”必须拥有8个英雄，否则无法参加战斗。<br/>5.活动结束后，根据积分兑换奖励。</font>", },
[12] = {id=12, title="夺宝规则", txt="1.夺宝系统全天开放<br></br>2.宝矿分三种：高级矿、中级矿、低级矿。<br></br>3.每日都有免费的刷新次数，VIP等级越高，刷新次数越多。<br></br>4.每个玩家可占领或抢夺2个矿。<br></br>5.每日有10次挑战次数，包括：占领挑战次数和抢夺挑战次数。<br></br>6.每次占领时间为8小时，结束后通过邮件发放收益。<br></br>7.已占领的矿产需派英雄驻防。<br></br>8.英雄驻防期间，不能参加其他夺宝战斗。<br></br>9.未开启保护的矿产会被抢夺！注意保护哦！<br></br>", },
}
